svg.script:

window.onload = function() {
    let cuadrado = document.getElementById('cuadrado');

    cuadrado.onclick = function() {
        let color = this.getAttribute('fill');

        if (color == "#00ff00"){
            this.setAttribute('fill', "#0000ff");
        } else {
            this.setAttribute('fill', "#00ff00");
        }
    }
}
